function addVAT(){var t=document.getElementById("vat-input").value,e=document.getElementById("input").value,n=e*(1+t/100),d=n-e;document.getElementById("output").textContent=n.toFixed(2),document.getElementById("outputVatdiff").textContent=d.toFixed(2),document.getElementById("amount").textContent="Inc VAT"}function removeVAT(){var t=document.getElementById("vat-input").value,e=document.getElementById("input").value,n=e/(1+t/100),d=n-e;document.getElementById("output").textContent=n.toFixed(2),document.getElementById("outputVatdiff").textContent=d.toFixed(2),document.getElementById("amount").textContent="Ex VAT"}function keyEventHandler(t){var e=t.keyCode;13===e?addVAT():16===e?removeVAT():40===e&&removeVAT()}document.addEventListener("DOMContentLoaded",function(){document.getElementById("addButton").addEventListener("click",addVAT),document.getElementById("removeButton").addEventListener("click",removeVAT),document.addEventListener("keydown",keyEventHandler,!1)});